                });

                page.on('pageerror', (err) => {
                    if (err.message.includes('localStorage') || err.message.includes('Access is denied')) {
                        return;
                    }
                    consoleErrors.push(`[RUNTIME_ERROR] ${err.message}`);
                });

                const response = await page.goto(url, { waitUntil: 'networkidle' });
                expect(response?.status()).toBe(200);

                const bodyText = await page.innerText('body');
                const pageTitle = await page.title();

                const isError =
                    pageTitle.toLowerCase().includes('404') ||
                    pageTitle.toLowerCase().includes('not found') ||
                    bodyText.includes('Not Found') ||
                    bodyText.includes('Application error') ||
                    bodyText.includes('Hydration failed') ||
                    bodyText.includes('Unhandled Runtime Error') ||
                    bodyText.includes('MISSING_MESSAGE');

                if (isError) {
                    await page.screenshot({ path: `qa-i18n/failures/error_${parsedUrl.pathname.replace(/\//g, '_')}.png` });
                    throw new Error(`Detected Error UI on valid route: ${url}`);
                }

                if (consoleErrors.length > 0) {
                    await page.screenshot({ path: `qa-i18n/failures/console_${parsedUrl.pathname.replace(/\//g, '_')}.png` });
                    throw new Error(`Console Errors detected on ${url}:\n${consoleErrors.join('\n')}`);
                }

                // SEO Check
                const locale = url.split('/')[3];
                const htmlLang = await page.getAttribute('html', 'lang');
                expect(htmlLang).toBe(locale);

                const canonical = (await page.getAttribute('link[rel="canonical"]', 'href')) || 'MISSING';
                const hreflangs = await page.locator('link[rel="alternate"][hreflang]').evaluateAll((links: Array<Element>) =>
                    links.map(l => l.getAttribute('hreflang') || '')
                );
                const description = (await page.getAttribute('meta[name="description"]', 'content')) || 'MISSING';

                seoResults.push({ url, lang: htmlLang || 'missing', canonical, hreflangs, title: pageTitle, description });

                // Link Sample Check
                const internalLinks = await page.locator('a[href^="/"]').evaluateAll((elements: Array<Element>) =>
                    elements.map(el => (el as HTMLAnchorElement).getAttribute('href'))
                );

                for (const link of internalLinks.slice(0, 3)) { // Reduced to 3 for speed
                    if (link && !link.startsWith('/api') && !link.includes('#')) {
                        const linkResponse = await page.request.get(link);
                        expect(linkResponse.status(), `Broken link: ${link} on ${url}`).toBe(200);
                    }
                }

                // Visual Smoke
                const viewport = page.viewportSize();
                await page.screenshot({ path: `qa-i18n/smoke/desktop_${locale}_${pathSuffix.replace(/\//g, '_')}.png` });

                if (viewport && viewport.width < 768) {
                    const menuBtn = page.locator('button[aria-label*="menu"], button[class*="menu-toggle"]').first();
                    if (await menuBtn.isVisible()) {
                        await menuBtn.click();
                        await page.waitForTimeout(300);
                        await page.screenshot({ path: `qa-i18n/smoke/mobile_menu_${locale}_${pathSuffix.replace(/\//g, '_')}.png` });
                    }
                }
            });
        } else {
            // Non-critical: Just a status check for baseline health
            test(`Status Check: ${url}`, async ({ page }) => {
                let response;
                let attempt = 0;
                const maxAttempts = 3;

                while (attempt < maxAttempts) {
                    response = await page.request.get(url);
                    if (response.status() !== 429) break;
                    attempt++;
                    await new Promise(resolve => setTimeout(resolve, 1000 * attempt));
                }

                expect(response?.status(), `Page ${url} is down!`).toBe(200);

                // Quick check for 404 text in body if status is 200 (Next.js sometimes does this)
                const text = await response?.text() || '';
                expect(text.toLowerCase()).not.toContain('>404<');
                expect(text).not.toContain('MISSING_MESSAGE');
            });
        }
    }
});

import { test, expect } from '@playwright/test';

const LOCALES = ['en', 'es', 'fr', 'de', 'zh', 'hi', 'ja', 'ko'];
const HOST = process.env.TEST_URL || 'http://localhost:3000';

test.describe('Quality Gate - Core Navigation & UI', () => {

    for (const locale of LOCALES) {
        test.describe(`Locale: ${locale}`, () => {
            const consoleErrors: string[] = [];

            test.beforeEach(async ({ page }) => {
                // Pre-accept cookies to avoid overlays and locator noise
                await page.addInitScript(() => {
                    try {
                        if (typeof window !== 'undefined' && window.localStorage) {
                            window.localStorage.setItem('omnigcloud_cookie_consent', 'accepted');
                        }
                    } catch (e) {
                        // Storage access may be denied in some CI environments or cross-origin scenarios
                        console.warn('Storage access denied:', e);
                    }
                });

                page.on('pageerror', err => {
                    // Ignore localStorage access errors as they are non-fatal for UI tests
                    if (err.message.includes('localStorage') || err.message.includes('Access is denied')) {
                        console.warn('[CI-WARN] Ignored Storage Error:', err.message);
                        return;
                    }
                    throw new Error(`Unhandled Exception: ${err.message}`);
                });

                // Capture console errors
                page.on('console', msg => {
                    const text = msg.text();
                    if (msg.type() === 'error') {
                        // Ignore common non-breaking errors or expected transient errors
                        if (text.includes('chrome-extension') ||
                            text.includes('Vercel') ||
                            text.includes('insights') ||
                            text.includes('404') ||
                            text.includes('429') ||
                            text.includes('favicon')) return;
                        consoleErrors.push(text);
                    }
                });
            });

            test.afterEach(() => {
                if (consoleErrors.length > 0) {
                    const errorMsg = `Console Errors found: ${consoleErrors.join(', ')}`;
                    consoleErrors.length = 0;
                    throw new Error(errorMsg);
                }
            });

            test.setTimeout(60000);

            test('initial block load & layout integrity', async ({ page, baseURL }) => {
                const HOST = baseURL || 'http://localhost:3000';
                await page.goto(`${HOST}/${locale}`);

                // Check for core elements
                await expect(page.locator('header')).toBeVisible();
                await expect(page.locator('footer')).toBeVisible();

                // Check for horizontal overflow (UI quality)
                const hasOverflow = await page.evaluate(() => {
                    return document.documentElement.scrollWidth > window.innerWidth;
                });
                expect(hasOverflow, `Locale ${locale} has horizontal overflow`).toBe(false);

                // Check for React Hydration errors in content
                const content = await page.content();
                expect(content).not.toContain('Hydration failed');
            });

            test('section navigation via hash', async ({ page, baseURL }) => {
                const HOST = baseURL || 'http://localhost:3000';
                // Test a known section like products#playground
                const url = `${HOST}/${locale}/products#playground`;
                await page.goto(url);
                await page.waitForTimeout(5000); // Wait for scroll animation to settle

                // Verify hash in URL
                expect(page.url()).toContain('#playground');

                // Bounding rect check for #playground
                const scrollInfo = await page.evaluate(() => {
                    const el = document.getElementById('playground');
                    if (!el) return { found: false, top: 0 };
                    const rect = el.getBoundingClientRect();
                    return { found: true, top: rect.top };
                });

                console.log(`[DEBUG] ${locale} #playground rect.top: ${scrollInfo.top}`);
                expect(scrollInfo.found, `Element #playground not found in ${locale}`).toBe(true);
                // Wider range to accommodate various viewport/header combinations and scroll-margin
                expect(scrollInfo.top).toBeGreaterThanOrEqual(-100);
                expect(scrollInfo.top).toBeLessThanOrEqual(1000);
            });

            test('language switch should preserve route and hash', async ({ page, baseURL }) => {
                if (locale === 'en') return; // Skip en-to-en switch test as it's redundant
                const HOST = baseURL || 'http://localhost:3000';

                await page.goto(`${HOST}/en/products#playground`);
                await page.waitForTimeout(2000);
                console.log(`[DEBUG] ${locale} current URL before switch: ${page.url()}`);

                // Open language switcher
                const switcher = page.locator('#language-switcher-btn');
                await switcher.waitFor({ state: 'visible', timeout: 10000 });
                await switcher.click({ force: true });
                await page.waitForTimeout(500); // Wait for dropdown animation

                // Select target locale by ID which is unique
                const langBtn = page.locator(`#lang-switch-${locale}`);
                await langBtn.waitFor({ state: 'attached', timeout: 5000 });
                console.log(`[DEBUG] Triggering click for ${locale}`);
                // Using dispatchEvent for maximum reliability regardless of viewport/overlays
                await langBtn.dispatchEvent('click');

                // Wait for URL to update
                const expectedUrlPart = `/${locale}/products#playground`;
                await expect(page).toHaveURL(new RegExp(expectedUrlPart), { timeout: 15000 });
                console.log(`[DEBUG] ${locale} switch successful. New URL: ${page.url()}`);

                await page.waitForTimeout(2000); // Wait for possible client-side scroll

                const scrollInfo = await page.evaluate(() => {
                    const el = document.getElementById('playground');
                    if (!el) return { found: false, top: 0 };
                    const rect = el.getBoundingClientRect();
                    return { found: true, top: rect.top };
                });

                console.log(`[DEBUG] ${locale} language switch #playground rect.top: ${scrollInfo.top}`);
                expect(scrollInfo.found, `Element #playground not found after switch to ${locale}`).toBe(true);
                // Allow a reasonable range for scroll position
                expect(scrollInfo.top).toBeGreaterThanOrEqual(-100);
                expect(scrollInfo.top).toBeLessThanOrEqual(1000);
            });
        });
    }
});

