export async function generateStaticParams() {
    const locales = ['en', 'es', 'fr', 'de', 'zh', 'hi', 'ja', 'ko'];
    const slugs = ['architecture', 'guide', 'api', 'governance'];

    return locales.flatMap((locale) =>
        slugs.map((slug) => ({ locale, slug }))
    );
}

