    },
    openGraph: {
      type: 'website',
      locale: localeMap[locale] || 'en_US',
      url: `${siteUrl}/${locale}`,
      title: 'OmniGCloud | Global Cloud-Agnostic Modernization',
      description: 'Autonomous, AI-governed cloud modernization for sovereign organizations.',
