import { NextRequest } from 'next/server';
import { withApiHarden, createSuccessResponse } from '@/lib/api-utils';
import pkg from '../../../../package.json';
import { config } from '@/config';
import { getRedis } from '@/lib/redis';
import { getSecret } from '@/secrets';

export async function GET(request: NextRequest) {
    return withApiHarden(request, async (req, { requestId }) => {
        // Determine active secrets provider
        const isLocal = config.env === 'local';
        const secretsProvider = isLocal ? 'process.env (Local .env)' : 'HashiCorp Vault (KV v2)';

        // Check Dependencies
        const dependencies: Record<string, any> = {
            redis: { status: 'UNKNOWN' },
            vault: { status: 'UNKNOWN' }
        };

        try {
            const redis = await getRedis();
            dependencies.redis.status = redis ? 'UP' : 'DEGRADED';
        } catch (e) {
            dependencies.redis.status = 'DOWN';
        }

        if (!isLocal) {
            try {
                // Try reading a known public or safe config key if any, or just check if it times out
                const testSecret = await getSecret('SYSTEM_HEALTH_CHECK');
                dependencies.vault.status = 'UP';
            } catch (e) {
                dependencies.vault.status = 'DOWN';
            }
        } else {
            dependencies.vault.status = 'SKIPPED (Local)';
        }

        // Overall status
        const isHealthy = Object.values(dependencies).every(d => d.status !== 'DOWN');

        return createSuccessResponse(requestId, {
            status: isHealthy ? 'ok' : 'degraded',
            system: {
                version: pkg.version,
                nodeEnv: process.env.NODE_ENV,
                appEnv: config.env,
                deploymentId: process.env.NEXT_PUBLIC_GIT_COMMIT || 'local-dev',
                uptimeSeconds: Math.floor(process.uptime()),
            },
            configuration: {
                secretsProvider,
                features: config.features,
            },
            dependencies
        });
    });
}

import { NextRequest } from 'next/server';
import { z } from 'zod';
import { withApiHarden, createSuccessResponse, createErrorResponse, handleZodError, handleSafeError } from '@/lib/api-utils';

// Optional import - only available after npm install @upstash/redis
let Redis: any = null;

try {
    // @ts-ignore - Optional dependency
    Redis = require('@upstash/redis').Redis;
} catch (e) {
    // Redis not installed yet
}

// Initialize Upstash Redis
const redis = Redis && process.env.REDIS_URL && process.env.REDIS_TOKEN
    ? new Redis({
        url: process.env.REDIS_URL,
        token: process.env.REDIS_TOKEN,
    })
    : null;

// Validation schema for query parameters
const LeadsQuerySchema = z.object({
    limit: z.string().optional().transform(val => Math.min(parseInt(val || '50'), 100)),
    offset: z.string().optional().transform(val => Math.max(parseInt(val || '0'), 0)),
});

export async function GET(req: NextRequest) {
    return withApiHarden(req, async (request, { requestId }) => {
        try {
            // Check authentication (optional - uncomment when you have auth set up)
            // const session = await auth();
            // if (!session?.user) {
            //     return createErrorResponse(requestId, 'UNAUTHORIZED', 'Authentication required', false, 401);
            // }

            if (!redis) {
                return createErrorResponse(
                    requestId,
                    'SERVICE_UNAVAILABLE',
                    'Database not configured',
                    false,
                    503
                );
            }

            // Validate query parameters
            const { searchParams } = new URL(request.url);
            const queryValidation = LeadsQuerySchema.safeParse({
                limit: searchParams.get('limit'),
                offset: searchParams.get('offset'),
            });

            if (!queryValidation.success) {
                return handleZodError(queryValidation.error, requestId);
            }

            const { limit, offset } = queryValidation.data;

            // Get all lead IDs
            const leadIds = await redis.lrange('leads:all', offset, offset + limit - 1);

            if (!leadIds || leadIds.length === 0) {
                return createSuccessResponse(requestId, {
                    leads: [],
                    total: 0,
                    limit,
                    offset,
                });
            }

            // Get lead details
            const leads = await Promise.all(
                leadIds.map(async (id: string) => {
                    const data = await redis.get(`lead:${id}`);
                    return data ? JSON.parse(data as string) : null;
                })
            );

            // Filter out null values
            const validLeads = leads.filter(lead => lead !== null);

            // Get total count
            const total = await redis.llen('leads:all');

            return createSuccessResponse(requestId, {
                leads: validLeads,
                total,
                limit,
                offset,
            });

        } catch (error) {
            return handleSafeError(error, requestId);
        }
    });
}

import type { NextAuthConfig } from "next-auth";
import Google from "next-auth/providers/google";
import Github from "next-auth/providers/github";
import Entra from "next-auth/providers/microsoft-entra-id";

// Define the configuration separately to be used in Edge Middleware
// This MUST NOT import 'next-auth/providers/email' or database adapters
export const authConfig = {
    providers: [
        Google({
            clientId: process.env.AUTH_GOOGLE_ID,
            clientSecret: process.env.AUTH_GOOGLE_SECRET,
            authorization: {
                params: {
                    prompt: "consent",
                    access_type: "offline",
                    response_type: "code",
                },
            },
        }),
        Entra({
            clientId: process.env.AUTH_ENTRA_ID,
            clientSecret: process.env.AUTH_ENTRA_SECRET,
            issuer: `https://login.microsoftonline.com/${process.env.AUTH_ENTRA_TENANT_ID}/v2.0`,
        }),
        Github({
            clientId: process.env.AUTH_GITHUB_ID,
            clientSecret: process.env.AUTH_GITHUB_SECRET,
        }),
    ],
    callbacks: {
        async jwt({ token, user, profile, account }) {
            if (user) {
                let role: "admin" | "billing" | "user" = "user";

                if (account?.provider === "microsoft-entra-id") {
                    const groups = (profile as any)?.groups as string[] | undefined;
                    if (groups) {
                        if (groups.includes(process.env.AD_GROUP_ADMIN || "")) role = "admin";
                        else if (groups.includes(process.env.AD_GROUP_BILLING || "")) role = "billing";
                    }
                }

                if (process.env.ADMIN_EMAILS?.split(",").includes(user.email || "")) {
                    role = "admin";
                }

                token.role = role;
            }
            return token;
        },
        async session({ session, token }) {
            if (session.user && token.role) {
                session.user.role = token.role as any;
            }
            return session;
        },
    },
    session: {
        strategy: "jwt",
    },
} satisfies NextAuthConfig;

import NextAuth, { type DefaultSession, type NextAuthConfig } from "next-auth";
import { UpstashRedisAdapter } from "@auth/upstash-redis-adapter";
import { Redis } from "@upstash/redis";
import { authConfig } from "./auth.config";

const redis = new Redis({
    url: process.env.REDIS_URL || "https://mock.upstash.io",
    token: process.env.REDIS_TOKEN || "mock_token",
});

export type UserRole = "admin" | "billing" | "user";

declare module "next-auth" {
    interface Session {
        user: {
            role: UserRole;
        } & DefaultSession["user"];
    }

    interface User {
        role?: UserRole;
    }
}

// Extend the basic authConfig with Node.js exclusive features (Adapter, Email)
const fullAuthConfig = {
    ...authConfig,
    adapter: UpstashRedisAdapter(redis),
    providers: [
        ...authConfig.providers,
    ],
    // We must merge callbacks if we want to add more logic, 
    // but typically the edge-compatible ones are sufficient for session/jwt.
    // If we need extra signIn logic for Magic Link rate limiting (which uses Redis),
    // we add it here.
    callbacks: {
        ...authConfig.callbacks,
        async signIn() {
            // Base signIn logic (already covers standard providers)
            return true;
        },
    },
    events: {
        async signIn({ user, account }) {
            const maskedEmail = (user as any).email?.replace(/^(.)(.*)(@.*)$/, (_: any, a: any, b: any, c: any) => a + b.replace(/./g, '*') + c);
            console.log(`[AUTH_AUDIT] Login Success | User: ${maskedEmail} | Provider: ${account?.provider} | Timestamp: ${new Date().toISOString()}`);
        },
        async signOut(data: any) {
            const maskedEmail = data.session?.user?.email?.replace(/^(.)(.*)(@.*)$/, (_: any, a: any, b: any, c: any) => a + b.replace(/./g, '*') + c);
            console.log(`[AUTH_AUDIT] Logout | User: ${maskedEmail || 'Unknown'} | Timestamp: ${new Date().toISOString()}`);
        },
    },
} satisfies NextAuthConfig;

export const { handlers, auth, signIn, signOut } = NextAuth(fullAuthConfig);

