export default function ContactForm({ translations }: { translations: any }) {
    const [status, setStatus] = useState<"idle" | "submitting" | "success" | "error">("idle");
    const [errorMessage, setErrorMessage] = useState("");

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setStatus("submitting");

        const formData = new FormData(e.currentTarget);
        const data = Object.fromEntries(formData.entries());

        try {
            const response = await fetch("/api/contact", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(data),
            });

            if (response.ok) {
                setStatus("success");
            } else {
                const err = await response.json();
                setErrorMessage(err.message || "Something went wrong.");
                setStatus("error");
            }
        } catch (err) {
            setErrorMessage("Failed to send message. Please try again later.");
            setStatus("error");
        }
    };

    if (status === "success") {
        return (
            <div style={{ textAlign: 'center' }}>
                <div style={{ height: '80px', width: '80px', background: 'rgba(16, 185, 129, 0.1)', color: '#10b981', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto 2rem' }}>
                    <Send size={40} />
                </div>
                <h3 style={{ color: 'white', fontSize: '1.8rem', fontWeight: 800, marginBottom: '1rem' }}>Sovereign Handshake</h3>
                <p style={{ color: 'rgba(255,255,255,0.6)' }}>Your briefing is under review. <br /> <span style={{ fontFamily: 'monospace', fontSize: '0.8rem', color: '#60efff' }}>TICKET_ID: SOV-77X-BETA</span></p>
                <button onClick={() => setStatus("idle")} style={{ marginTop: '2rem', color: '#60efff', background: 'none', border: 'none', fontWeight: 700, cursor: 'pointer' }}>INITIATE NEW SESSION</button>
            </div>
        );
    }

    return (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1.2rem' }}>
            <div className="grid-2" style={{ gap: '1.2rem' }}>
                <input name="firstName" required type="text" placeholder="First Name" style={{ background: 'rgba(255,255,255,0.03)', border: '1px solid rgba(255,255,255,0.1)', padding: '1rem', borderRadius: '1rem', color: 'white', width: '100%' }} />
                <input name="lastName" required type="text" placeholder="Last Name" style={{ background: 'rgba(255,255,255,0.03)', border: '1px solid rgba(255,255,255,0.1)', padding: '1rem', borderRadius: '1rem', color: 'white', width: '100%' }} />
            </div>
            {/* Honeypot field for bot protection */}
            <div style={{ display: 'none' }}>
                <input name="website" type="text" tabIndex={-1} autoComplete="off" />
            </div>
            <input name="email" required type="email" placeholder="Work Email" style={{ background: 'rgba(255,255,255,0.03)', border: '1px solid rgba(255,255,255,0.1)', padding: '1rem', borderRadius: '1rem', color: 'white', width: '100%' }} />
            <textarea name="message" required rows={4} placeholder="Describe your 2025 Modernization Goals (AWS, Azure, OCP, NeoCloud)..." style={{ background: 'rgba(255,255,255,0.03)', border: '1px solid rgba(255,255,255,0.1)', padding: '1rem', borderRadius: '1rem', color: 'white', resize: 'none', width: '100%' }}></textarea>
            <button disabled={status === "submitting"} type="submit" className="btn-primary" style={{ padding: '1.2rem', borderRadius: '1rem', fontWeight: 900, background: '#3b82f6', borderColor: '#3b82f6', width: '100%' }}>
                {status === "submitting" ? "ENCRYPTING..." : "SUBMIT ARCHITECT BRIEF"}
            </button>
            {status === "error" && (
                <p style={{ color: '#ef4444', fontSize: '0.8rem', marginTop: '0.5rem', textAlign: 'center' }}>{errorMessage}</p>
            )}
        </form>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Activity, Globe, Zap, Server } from 'lucide-react';

interface NodeStatus {
    id: string;
    region: string;
    provider: string;
    latency: number;
    status: 'optimal' | 'stable' | 'degraded';
}

const REGIONS = [
    { id: 'us-east', name: 'US East (N. Virginia)', provider: 'AWS' },
    { id: 'us-west', name: 'US West (Oregon)', provider: 'AWS' },
    { id: 'eu-west', name: 'Europe (Frankfurt)', provider: 'GCP' },
    { id: 'ap-south', name: 'Asia Pacific (Mumbai)', provider: 'Azure' },
    { id: 'ap-east', name: 'Asia Pacific (Tokyo)', provider: 'OCP' },
    { id: 'sa-east', name: 'South America (S├úo Paulo)', provider: 'Oracle' }
];

export default function LatencyMonitor() {
    const [nodes, setNodes] = useState<NodeStatus[]>([]);
    const [avgLatency, setAvgLatency] = useState(0);

    useEffect(() => {
        // Initial state
        const initialNodes = REGIONS.map(r => ({
            id: r.id,
            region: r.name,
            provider: r.provider,
            latency: Math.floor(Math.random() * 40) + 10,
            status: 'optimal' as const
        }));
        setNodes(initialNodes);

        // Update loop to simulate live latency analysis
        const interval = setInterval(() => {
            setNodes(prev => {
                const updated = prev.map(node => {
                    const jitter = Math.floor(Math.random() * 5) - 2;
                    const newLatency = Math.max(8, node.latency + jitter);
                    let status: NodeStatus['status'] = 'optimal';
                    if (newLatency > 60) status = 'degraded';
                    else if (newLatency > 35) status = 'stable';

                    return { ...node, latency: newLatency, status };
                });

                const avg = updated.reduce((acc, n) => acc + n.latency, 0) / updated.length;
                setAvgLatency(avg);
                return updated;
            });
        }, 2000);

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="glass-panel" style={{
            padding: '1.5rem',
            borderRadius: '1.25rem',
            border: '1px solid var(--card-border)',
            background: 'var(--bg-surface-2)',
            boxShadow: '0 8px 32px rgba(0,0,0,0.2)'
        }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                    <div style={{ padding: '0.5rem', background: 'rgba(59, 130, 246, 0.1)', borderRadius: '0.75rem', color: 'var(--primary)' }}>
                        <Zap size={20} />
                    </div>
                    <div>
                        <h4 style={{ fontSize: '1rem', fontWeight: 800, margin: 0 }}>Global Mesh Latency</h4>
                        <p style={{ fontSize: '0.7rem', color: 'var(--muted)', margin: 0 }}>OmniGCloud Autonomous Discovery</p>
                    </div>
                </div>
                <div style={{ textAlign: 'right' }}>
                    <div style={{ fontSize: '1.25rem', fontWeight: 900, color: 'var(--primary)', lineHeight: 1 }}>{avgLatency.toFixed(1)}<span style={{ fontSize: '0.7rem', opacity: 0.6 }}>ms</span></div>
                    <div style={{ fontSize: '0.6rem', color: 'var(--color-success)', fontWeight: 800 }}>MESH_HEALTH: 100%</div>
                </div>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
                {nodes.map(node => (
                    <div key={node.id} style={{
                        padding: '0.75rem',
                        background: 'rgba(255,255,255,0.02)',
                        border: '1px solid rgba(255,255,255,0.05)',
                        borderRadius: '0.75rem',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between'
                    }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                            <div style={{
                                width: '8px',
                                height: '8px',
                                borderRadius: '50%',
                                background: node.status === 'optimal' ? '#10b981' : node.status === 'stable' ? '#f59e0b' : '#ef4444',
                                boxShadow: `0 0 8px ${node.status === 'optimal' ? '#10b981' : '#f59e0b'}`
                            }} />
                            <div>
                                <div style={{ fontSize: '0.8rem', fontWeight: 700, lineHeight: 1 }}>{node.region}</div>
                                <div style={{ fontSize: '0.65rem', color: 'var(--muted)', marginTop: '0.2rem' }}>{node.provider} Node</div>
                            </div>
                        </div>
                        <div style={{ fontFamily: 'monospace', fontSize: '0.85rem', fontWeight: 700, color: node.status === 'optimal' ? '#10b981' : 'var(--foreground)' }}>
                            {node.latency}ms
                        </div>
                    </div>
                ))}
            </div>

            <div style={{ marginTop: '1.5rem', borderTop: '1px solid var(--card-border)', paddingTop: '1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.65rem', fontWeight: 700, textTransform: 'uppercase', letterSpacing: '0.05em', color: 'var(--muted)' }}>
                    <span>Mesh Scan Pattern</span>
                    <span style={{ color: 'var(--primary)' }}>Real-time Analysis Active</span>
                </div>
                <div style={{ marginTop: '0.5rem', height: '2px', background: 'rgba(59, 130, 246, 0.1)', borderRadius: '1px', overflow: 'hidden' }}>
                    <div style={{
                        width: '40%',
                        height: '100%',
                        background: 'var(--primary)',
                        animation: 'shimmer 2s infinite linear'
                    }} />
                </div>
            </div>

            <style jsx>{`
                @keyframes shimmer {
                    0% { transform: translateX(-100%); }
                    100% { transform: translateX(300%); }
                }
            `}</style>
        </div>
    );
}

