"use client";

import React, { useState, useEffect } from 'react';
import dynamic from 'next/dynamic';
import { Activity, Server, ShieldAlert } from 'lucide-react';

const LatencyChart = dynamic(() => import('../../charts/MetricsCharts').then(mod => mod.LatencyChart), { ssr: false, loading: () => <ChartSkeleton /> });
const RpsChart = dynamic(() => import('../../charts/MetricsCharts').then(mod => mod.RpsChart), { ssr: false, loading: () => <ChartSkeleton /> });
const ErrorRateChart = dynamic(() => import('../../charts/MetricsCharts').then(mod => mod.ErrorRateChart), { ssr: false, loading: () => <ChartSkeleton /> });

function ChartSkeleton() {
    return <div className="w-full h-full bg-slate-800/20 animate-pulse rounded-xl" />;
}

export default function SecurityPerformanceSection() {
    const [data, setData] = useState<any[]>([]);

    useEffect(() => {
        // Generate mock historical data
        const now = new Date();
        const history = Array.from({ length: 20 }).map((_, i) => {
            const time = new Date(now.getTime() - (20 - i) * 60000);
            return {
                time: time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                p50: Math.floor(Math.random() * 20 + 20),
                p99: Math.floor(Math.random() * 50 + 80),
                rps: Math.floor(Math.random() * 500 + 1500),
                errorRate: Number((Math.random() * 0.02).toFixed(4))
            };
        });
        setData(history);

        const interval = setInterval(() => {
            setData(prev => {
                const nextTime = new Date();
                const newEntry = {
                    time: nextTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    p50: Math.floor(Math.random() * 20 + 20),
                    p99: Math.floor(Math.random() * 50 + 80),
                    rps: Math.floor(Math.random() * 500 + 1500),
                    errorRate: Number((Math.random() * 0.02).toFixed(4))
                };
                return [...prev.slice(1), newEntry];
            });
        }, 3000);

        return () => clearInterval(interval);
    }, []);

    return (
        <section className="glass-panel p-8 rounded-xl mb-8">
            <h2 className="text-3xl font-bold mb-6">Real-Time Threat Latency</h2>
            <p className="text-zinc-700 dark:text-zinc-300 mb-8">
                Monitoring global ingress points for anomalous latency spikes indicating DDoS or probing attacks.
            </p>

            <div className="grid md:grid-cols-2 gap-6 h-auto">
                {/* Latency Card */}
                <div className="bg-slate-900/40 border border-slate-800 rounded-xl p-6 flex flex-col min-h-[350px] min-w-0">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="font-bold text-slate-200 flex items-center gap-2">
                            <Activity size={18} className="text-blue-400" /> Cleaning Latency
                        </h3>
                        <span className="text-xs font-mono text-slate-500">Live</span>
                    </div>
                    <div className="h-[300px] w-full min-w-0">
                        <LatencyChart data={data} />
                    </div>
                </div>

                {/* Error Rate Card */}
                <div className="bg-slate-900/40 border border-slate-800 rounded-xl p-6 flex flex-col min-h-[350px] min-w-0">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="font-bold text-slate-200 flex items-center gap-2">
                            <ShieldAlert size={18} className="text-red-400" /> WAF Block Rate
                        </h3>
                        <span className="text-xs font-mono text-slate-500">SLA Monitor</span>
                    </div>
                    <div className="h-[300px] w-full min-w-0">
                        <ErrorRateChart data={data} />
                    </div>
                </div>
            </div>
        </section>
    );
}

"use client";

import { useState, useEffect } from 'react';

export default function CloudCostEstimator() {
    const [vms, setVms] = useState(25);
    const [storage, setStorage] = useState(5000); // GB
    const [gpuUnits, setGpuUnits] = useState(0);
    const [estimates, setEstimates] = useState<any[]>([]);
    const [loading, setLoading] = useState(false);

    const fetchEstimates = async () => {
        setLoading(true);
        try {
            const res = await fetch('/api/billing', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ vms, storage, gpuUnits })
            });
            const data = await res.json();
            if (data.status === 'success') {
                setEstimates(data.estimates);
            }
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        const debounce = setTimeout(() => {
            fetchEstimates();
        }, 500);
        return () => clearTimeout(debounce);
    }, [vms, storage, gpuUnits]);

    return (
        <div style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
            <h3 style={{ fontSize: '1.1rem', marginBottom: '1.5rem', color: '#60efff', fontWeight: 800, borderBottom: '1px solid rgba(96, 239, 255, 0.2)', paddingBottom: '0.75rem', textTransform: 'uppercase', letterSpacing: '0.05em' }}>Enterprise Billing Comparison</h3>

            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '2rem', marginBottom: '3rem' }}>
                <div>
                    <label style={{ display: 'block', marginBottom: '0.75rem', fontWeight: '700', color: 'rgba(255,255,255,0.6)', fontSize: '0.8rem', textTransform: 'uppercase' }}>Compute (vCPU)</label>
                    <input type="range" min="10" max="500" value={vms} onChange={(e) => setVms(parseInt(e.target.value))} style={{ width: '100%', accentColor: '#60efff' }} />
                    <div style={{ textAlign: 'center', fontWeight: 800, color: 'white', marginTop: '0.5rem' }}>{vms} Units</div>
                </div>

                <div>
                    <label style={{ display: 'block', marginBottom: '0.75rem', fontWeight: '700', color: 'rgba(255,255,255,0.6)', fontSize: '0.8rem', textTransform: 'uppercase' }}>Storage (GB)</label>
                    <input type="range" min="500" max="50000" step="500" value={storage} onChange={(e) => setStorage(parseInt(e.target.value))} style={{ width: '100%', accentColor: '#60efff' }} />
                    <div style={{ textAlign: 'center', fontWeight: 800, color: 'white', marginTop: '0.5rem' }}>{storage} GB</div>
                </div>

                <div>
                    <label style={{ display: 'block', marginBottom: '0.75rem', fontWeight: '700', color: 'rgba(255,255,255,0.6)', fontSize: '0.8rem', textTransform: 'uppercase' }}>GPU Nodes</label>
                    <input type="range" min="0" max="32" value={gpuUnits} onChange={(e) => setGpuUnits(parseInt(e.target.value))} style={{ width: '100%', accentColor: '#60efff' }} />
                    <div style={{ textAlign: 'center', fontWeight: 800, color: 'white', marginTop: '0.5rem' }}>{gpuUnits} A100/H100</div>
                </div>
            </div>

            <div style={{ background: 'rgba(0,0,0,0.3)', padding: '1.5rem', borderRadius: '1.5rem', border: '1px solid rgba(255,255,255,0.05)', opacity: loading ? 0.6 : 1, transition: 'opacity 0.2s', flex: 1 }}>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '1.5rem' }}>
                    {estimates.map(est => {
                        const isBestValue = est.id === 'neocloud';
                        return (
                            <div key={est.id} style={{
                                padding: '1.5rem',
                                borderRadius: '1rem',
                                background: isBestValue ? 'rgba(96, 239, 255, 0.05)' : 'rgba(255,255,255,0.03)',
                                border: isBestValue ? '2px solid #60efff' : '1px solid rgba(255,255,255,0.1)',
                                position: 'relative'
                            }}>
                                {isBestValue && <span style={{ position: 'absolute', top: '0', right: '0', background: '#60efff', color: '#0a2540', fontSize: '0.6rem', padding: '0.2rem 0.6rem', borderRadius: '0 0.8rem 0 0.8rem', fontWeight: 800 }}>BEST VALUE</span>}
                                <div style={{ fontSize: '0.9rem', color: isBestValue ? '#60efff' : 'rgba(255,255,255,0.5)', fontWeight: 700, marginBottom: '0.5rem' }}>{est.name}</div>
                                <div style={{ fontSize: '1.8rem', fontWeight: 900, color: 'white' }}>${est.monthlyCost.toLocaleString()}</div>
                                <div style={{ fontSize: '0.7rem', color: isBestValue ? 'rgba(96,239,255,0.7)' : 'rgba(255,255,255,0.3)', marginTop: '0.5rem' }}>{est.advantage}</div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
}

