    const [isMounted, setIsMounted] = useState(false);
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
    const savings = [12000, 18500, 24000, 31000, 38500, 45000];
    const maxSaving = Math.max(...savings);

    useEffect(() => {
        setIsMounted(true);
    }, []);

    if (!isMounted) return <div style={{ width: '100%', height: '100%', minHeight: '200px' }}></div>;

    return (
        <div style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column' }}>
            <div style={{ flex: 1, position: 'relative', display: 'flex', alignItems: 'flex-end', gap: '0.5rem', padding: '1rem 0' }}>
                {savings.map((val, i) => (
                    <div
                        key={i}
                        style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '0.5rem', cursor: 'pointer' }}
                        onMouseEnter={() => setHoveredIndex(i)}
                        onMouseLeave={() => setHoveredIndex(null)}
                    >
                        <div style={{
                            width: '100%',
                            height: `${(val / maxSaving) * 100}%`,
                            background: hoveredIndex === i
                                ? 'linear-gradient(180deg, #60efff 0%, var(--primary) 100%)'
                                : 'linear-gradient(180deg, var(--primary) 0%, var(--color-accent-purple) 100%)',
                            borderRadius: '0.5rem 0.5rem 0 0',
                            position: 'relative',
                            minHeight: '20px',
                            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                            transform: hoveredIndex === i ? 'scaleY(1.05)' : 'scaleY(1)',
                            boxShadow: hoveredIndex === i ? '0 0 20px rgba(96, 239, 255, 0.5)' : 'none'
                        }}>
                            <div style={{
                                position: 'absolute',
                                top: '-2rem',
                                left: '50%',
                                transform: 'translateX(-50%)',
                                fontSize: hoveredIndex === i ? '0.75rem' : '0.65rem',
                                fontWeight: 900,
                                color: hoveredIndex === i ? '#60efff' : 'var(--primary)',
                                whiteSpace: 'nowrap',
                                transition: 'all 0.3s ease'
                            }}>
                                ${(val / 1000).toFixed(0)}k
                            </div>
                        </div>
                        <div style={{
                            fontSize: '0.6rem',
                            opacity: hoveredIndex === i ? 1 : 0.5,
                            fontWeight: hoveredIndex === i ? 800 : 700,
                            transition: 'all 0.3s ease'
                        }}>
                            {months[i]}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

// Real-time updating ROI Gauge
export const LiveROIGauge = ({ value = 342 }: { value?: number }) => {
    const [currentValue, setCurrentValue] = useState(0);
    const [isMounted, setIsMounted] = useState(false);
    const percentage = Math.min(value / 500 * 100, 100);

    useEffect(() => {
        setIsMounted(true);
    }, []);

    useEffect(() => {
        if (!isMounted) return;
        const timer = setTimeout(() => {
            if (currentValue < value) {
                setCurrentValue(prev => Math.min(prev + 5, value));
            }
        }, 30);
        return () => clearTimeout(timer);
    }, [currentValue, value, isMounted]);

    if (!isMounted) return <div style={{ width: '100%', height: '100%', minHeight: '180px' }}></div>;

    return (
        <div style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative' }}>
            <svg width="180" height="180" viewBox="0 0 180 180">
                <circle cx="90" cy="90" r="70" fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="12" />
                <circle
                    cx="90"
                    cy="90"
                    r="70"
                    fill="none"
                    stroke="url(#roiGradient)"
                    strokeWidth="12"
                    strokeDasharray={`${2 * Math.PI * 70 * (currentValue / 500)} ${2 * Math.PI * 70}`}
                    strokeLinecap="round"
                    transform="rotate(-90 90 90)"
                    style={{ transition: 'stroke-dasharray 0.5s ease-out' }}
                />
                <defs>
                    <linearGradient id="roiGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                        <stop offset="0%" stopColor="var(--primary)" />
                        <stop offset="100%" stopColor="#10b981" />
                    </linearGradient>
                </defs>
            </svg>
            <div style={{ position: 'absolute', textAlign: 'center' }}>
                <div style={{ fontSize: '2.5rem', fontWeight: 950, color: 'var(--primary)' }}>{currentValue}%</div>
                <div style={{ fontSize: '0.7rem', opacity: 0.5, fontWeight: 700, textTransform: 'uppercase' }}>ROI</div>
            </div>
        </div>
    );
};

// Pulsing Security Score
export const PulsingSecurityScore = ({ score = 94 }: { score?: number }) => {
    const [isPulsing, setIsPulsing] = useState(false);
    const [isMounted, setIsMounted] = useState(false);
    const getColor = (s: number) => {
        if (s >= 90) return '#10b981';
        if (s >= 70) return '#f59e0b';
        return '#ef4444';
    };

    useEffect(() => {
        setIsMounted(true);
    }, []);

    useEffect(() => {
        if (!isMounted) return;
        const interval = setInterval(() => {
            setIsPulsing(true);
            setTimeout(() => setIsPulsing(false), 1000);
        }, 3000);
        return () => clearInterval(interval);
    }, [isMounted]);

    if (!isMounted) return <div style={{ width: '100%', height: '100%', minHeight: '150px' }}></div>;

    return (
        <div style={{ width: '100%', height: '100%', display: 'flex', flexDirection: 'column', justifyContent: 'center', gap: '1rem' }}>
            <div style={{ textAlign: 'center', position: 'relative' }}>
                <div style={{
                    fontSize: '3rem',
                    fontWeight: 950,
                    color: getColor(score),
                    transition: 'all 0.3s ease',
                    transform: isPulsing ? 'scale(1.1)' : 'scale(1)'
                }}>
                    {score}
                </div>
                <div style={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    width: '80px',
                    height: '80px',
                    borderRadius: '50%',
                    border: `2px solid ${getColor(score)}`,
                    animation: 'pulse-ring 1s ease-out',
                    opacity: isPulsing ? 1 : 0,
                    pointerEvents: 'none'
                }} />
                <div style={{ fontSize: '0.7rem', opacity: 0.5, fontWeight: 700, textTransform: 'uppercase' }}>Security Score</div>
            </div>
            <div style={{ height: '12px', background: 'rgba(255,255,255,0.05)', borderRadius: '1rem', overflow: 'hidden' }}>
                <div style={{
                    width: `${score}%`,
                    height: '100%',
                    background: `linear-gradient(90deg, ${getColor(score)} 0%, ${getColor(score)}dd 100%)`,
                    borderRadius: '1rem',
                    transition: 'width 1s ease'
                }}></div>
            </div>
            <style jsx>{`
                @keyframes pulse-ring {
                    0% {
                        transform: translate(-50%, -50%) scale(0.8);
                        opacity: 1;
                    }
                    100% {
                        transform: translate(-50%, -50%) scale(1.5);
                        opacity: 0;
                    }
                }
            `}</style>
        </div>
    );
};

