/**
 * Helper to build full URL with locale
 */
export function buildNavUrl(item: NavItem, locale: string): string {
    const base = `/${locale}${item.route || ''}`;
    return item.hash ? `${base}#${item.hash}` : base;
}

/**
 * Helper to check if nav item is currently active
 */
export function isNavItemActive(item: NavItem, currentPath: string, currentHash: string): boolean {
    if (item.type === 'section') {
        const pathMatches = currentPath.includes(item.route || '');
        const hashMatches = currentHash === item.hash;
        return pathMatches && hashMatches;
    }
    return currentPath.includes(item.route || '');
}

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import createMiddleware from 'next-intl/middleware';

import { getRateLimiter } from './lib/rate-limit';

const locales = ['en', 'es', 'fr', 'de', 'zh', 'hi', 'ja', 'ko'];
const defaultLocale = 'en';

const limiter = getRateLimiter();

const intlMiddleware = createMiddleware({
  locales,
  defaultLocale,
});

export async function coreMiddleware(request: NextRequest) {
  const ip =
    request.headers.get('x-forwarded-for')?.split(',')[0]?.trim() ||
    (request as any).ip ||
    'unknown';

  const rateLimit = await limiter.check(ip);

  if (!rateLimit.allowed) {
    return new NextResponse('Too Many Requests', {
      status: 429,
      headers: {
        'Retry-After': String(rateLimit.retryAfter ?? 60),
      },
    });
  }


  // Run the i18n middleware for all other requests
  return intlMiddleware(request);
}

"use client";

import { useState, useCallback, useEffect } from 'react';
import { useTranslations, useLocale } from 'next-intl';
import { SALES_EMAIL } from '@/config/emails';

const RATE_LIMIT_KEY = 'omnigcloud_contact_sales_limit';
const MAX_ATTEMPTS = 3;
const WINDOW_MS = 5 * 60 * 1000; // 5 minutes

interface RateLimitData {
    attempts: number;
    startTime: number;
}

export function useContactSales() {
    const t = useTranslations('ContactSales');
    const navT = useTranslations('Header.nav');
    const locale = useLocale();
    const [isFallbackVisible, setIsFallbackVisible] = useState(false);
    const [isRateLimited, setIsRateLimited] = useState(false);
    const [lastError, setLastError] = useState<string | null>(null);

    const checkRateLimit = useCallback(() => {
        const stored = localStorage.getItem(RATE_LIMIT_KEY);
        const now = Date.now();

        if (stored) {
            const data: RateLimitData = JSON.parse(stored);
            if (now - data.startTime > WINDOW_MS) {
                // Reset window
                const newData = { attempts: 1, startTime: now };
                localStorage.setItem(RATE_LIMIT_KEY, JSON.stringify(newData));
                setIsRateLimited(false);
                return true;
            }

            if (data.attempts >= MAX_ATTEMPTS) {
                setIsRateLimited(true);
                return false;
            }

            // Increment attempts
            const newData = { ...data, attempts: data.attempts + 1 };
            localStorage.setItem(RATE_LIMIT_KEY, JSON.stringify(newData));
            return true;
        }

        // First attempt
        const newData = { attempts: 1, startTime: now };
        localStorage.setItem(RATE_LIMIT_KEY, JSON.stringify(newData));
        return true;
    }, []);

    const handleContactSales = useCallback(() => {
        if (!checkRateLimit()) {
            return;
        }

        const subject = t('subject');
        const bodyIntro = t('bodyIntro');
        const currentUrl = typeof window !== 'undefined' ? window.location.href : '';
        const referrer = typeof document !== 'undefined' ? document.referrer || 'direct' : 'direct';

        const body = `${bodyIntro}\n\n` +
            `--- Context ---\n` +
            `Page: ${currentUrl}\n` +
            `Locale: ${locale}\n` +
            `Referrer: ${referrer}\n` +
            `Time: ${new Date().toISOString()}`;

        const mailtoUrl = `mailto:${SALES_EMAIL}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;

        // Attempt to open mailto
        const start = Date.now();
        window.location.href = mailtoUrl;

        // Fallback detection logic
        // If the window doesn't lose focus within 500ms, it's likely the mailto failed
        setTimeout(() => {
            if (Date.now() - start < 1000) {
                // We don't necessarily show fallback immediately because mailto might still be opening
                // But we can enable the fallback state if they click again or stay on page
                setIsFallbackVisible(true);
            }
        }, 500);

    }, [t, locale, checkRateLimit]);

    const copyToClipboard = useCallback(async () => {
        try {
            await navigator.clipboard.writeText(SALES_EMAIL);
            return true;
        } catch (err) {
            console.error('Failed to copy email:', err);
            return false;
        }
    }, []);

    const getWhatsAppUrl = useCallback(() => {
        const bodyIntro = t('bodyIntro');
        const currentUrl = typeof window !== 'undefined' ? window.location.href : '';
        const message = `${bodyIntro}\n\nInquiry from: ${currentUrl} (${locale})`;
        return `https://wa.me/1234567890?text=${encodeURIComponent(message)}`;
    }, [t, locale]);

    return {
        handleContactSales,
        isFallbackVisible,
        setIsFallbackVisible,
        isRateLimited,
        copyToClipboard,
        getWhatsAppUrl,
        salesEmail: SALES_EMAIL,
        translations: {
            title: t('fallbackTitle'),
            instruction: t('fallbackInstruction'),
            copyLabel: t('copyLabel'),
            whatsappLabel: t('whatsappLabel'),
            rateLimitMessage: t('rateLimitMessage'),
            copyToast: t('copyToast'),
            ctaLabel: navT('contactSales')
        }
    };
}

import { getRequestConfig } from 'next-intl/server';
import { logMissingKey } from '../lib/i18n-logger';

function deepMerge(target: any, source: any) {
    const output = { ...target };
    if (isObject(target) && isObject(source)) {
        Object.keys(source).forEach(key => {
            if (isObject(source[key])) {
                if (!(key in target)) {
                    Object.assign(output, { [key]: source[key] });
                } else {
                    output[key] = deepMerge(target[key], source[key]);
                }
            } else {
                Object.assign(output, { [key]: source[key] });
            }
        });
    }
    return output;
}

function isObject(item: any) {
    return (item && typeof item === 'object' && !Array.isArray(item));
}

export default getRequestConfig(async ({ requestLocale }) => {
    let locale = await requestLocale;

    if (!locale || !['en', 'es', 'fr', 'de', 'zh', 'hi', 'ja', 'ko'].includes(locale)) {
        locale = 'en';
    }

    // Load default 'en' messages as fallback
    const defaultMessages = (await import(`../../messages/en.json`)).default;

    let userMessages = {};
    if (locale !== 'en') {
        try {
            userMessages = (await import(`../../messages/${locale}.json`)).default;
        } catch (error) {
            console.error(`Could not load messages for locale: ${locale}`, error);
        }
    }

    // Deep merge: user messages override defaults. 
    const messages = deepMerge(defaultMessages, userMessages);

    return {
        locale,
        messages,
        onError(error) {
            if (error.code === 'MISSING_MESSAGE') {
                const key = error.message.match(/key "([^"]+)"/)?.[1] || 'unknown';
                logMissingKey(locale, key);
            } else {
                console.error(error);
            }
        },
        getMessageFallback({ namespace, key, error }) {
            const fullKey = namespace ? `${namespace}.${key}` : key;
            // Fallback to English if possible, otherwise return the key
            const path = fullKey.split('.');
            let val: any = defaultMessages;
            for (const k of path) {
                val = val?.[k];
            }
            return typeof val === 'string' ? val : fullKey;
        }
    };
});

import { NextResponse, NextRequest } from 'next/server';
import { v4 as uuidv4 } from 'uuid';
import { getRateLimiter } from './rate-limit';
import { logger } from './logger';
import { metricsHooks } from './metrics';
import { AppError, normalizeError, RateLimitError } from './errors';

export interface ApiResponse<T = any> {
    requestId: string;
    status: 'ok' | 'error';
    timestamp: string;
    data?: T;
    error?: {
        code: string;
        message: string;
        retryable: boolean;
    };
}

const limiter = getRateLimiter();

export async function withApiHarden(
    request: NextRequest,
    handler: (req: NextRequest, context: { requestId: string }) => Promise<NextResponse>
) {
    const requestId = request.headers.get('x-request-id') || uuidv4();
    const startTime = Date.now();
    const method = request.method;
    const url = new URL(request.url);
    const route = url.pathname;

    // Extract IP for rate limiting
    const ip = request.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown';

    try {
        // 1. Rate Limiting (endpoint-specific)
        const rateLimit = await limiter.check(ip, route);
        if (!rateLimit.allowed) {
            logger.warn('Rate limit exceeded', { requestId, route, method });
            metricsHooks.trackRateLimit(ip, false);

            return NextResponse.json(
                {
                    requestId,
                    status: 'error',
                    timestamp: new Date().toISOString(),
                    error: {
                        code: 'TOO_MANY_REQUESTS',
                        message: 'Rate limit exceeded. Please try again later.',
                        retryable: true,
                    },
                },
                {
                    status: 429,
                    headers: {
                        'Retry-After': String(rateLimit.retryAfter ?? 60),
                        'X-RateLimit-Remaining': String(rateLimit.remaining ?? 0)
                    }
                }
            );
        }
        metricsHooks.trackRateLimit(ip, true);

        // 2. Execute Handler
        const response = await handler(request, { requestId });
        const duration = Date.now() - startTime;

        // 3. Log and track metrics
        logger.http({
            requestId,
            method,
            route,
            status: response.status,
            duration,
        });

        metricsHooks.trackRequest(method, route, response.status, duration);

        return response;

    } catch (error: any) {
        const duration = Date.now() - startTime;

        logger.error('API handler error', {
            requestId,
            route,
            method,
            error: error.message,
            duration,
        });

        metricsHooks.trackError(route, error.name || 'UnknownError');
        metricsHooks.trackRequest(method, route, 500, duration);

        return NextResponse.json(
            {
                requestId,
                status: 'error',
                timestamp: new Date().toISOString(),
                error: {
                    code: 'INTERNAL_SERVER_ERROR',
                    message: 'An unexpected error occurred.',
                },
            },
            { status: 500 }
        );
    }
}

export function createSuccessResponse<T>(requestId: string, data: T, status = 200) {
    return NextResponse.json(
        {
            requestId,
            status: 'ok' as const,
            timestamp: new Date().toISOString(),
            data,
        },
        { status }
    );
}

export function createErrorResponse(
    requestId: string,
    code: string,
    message: string,
    retryable: boolean = false,
    status = 400
) {
    return NextResponse.json(
        {
            requestId,
            status: 'error' as const,
            timestamp: new Date().toISOString(),
            error: {
                code,
                message,
                retryable,
            },
        },
        { status }
    );
}

/**
 * Handle Zod validation errors
 */
export function handleZodError(error: any, requestId: string) {
    const firstError = error.errors?.[0];
    const field = firstError?.path?.join('.') || 'input';
    const message = `Invalid ${field}: ${firstError?.message || 'validation failed'}`;

    return createErrorResponse(
        requestId,
        'VALIDATION_ERROR',
        message,
        false,
        422
    );
}

/**
 * Safe error handler that never exposes stack traces or internal details
 */
export function handleSafeError(error: unknown, requestId: string) {
    // Log full error internally (safe logging without PII)
    if (process.env.NODE_ENV !== 'production') {
        console.error('[API Error]', { requestId, error });
    } else {
        // Production: log only safe information
        logger.error('API error', {
            requestId,
            message: error instanceof Error ? error.message : 'Unknown error',
        });
    }

    // Return generic error to client (no stack traces, no internal details)
    return createErrorResponse(
        requestId,
        'INTERNAL_ERROR',
        'An unexpected error occurred. Please try again later.',
        true,
        500
    );
}

/**
 * Audit log system for security and compliance
 * In-memory storage with interface for future DB persistence
 */

export type AuditEventType =
    | 'auth.login'
    | 'auth.logout'
    | 'auth.failed'
    | 'auth.mfa_enabled'
    | 'auth.mfa_disabled'
    | 'user.created'
    | 'user.updated'
    | 'user.deleted'
    | 'access.granted'
    | 'access.denied'
    | 'data.exported'
    | 'data.deleted'
    | 'settings.updated'
    | 'api.key_created'
    | 'api.key_revoked';

export interface AuditEvent {
    id: string;
    timestamp: number;
    eventType: AuditEventType;
    userId?: string;
    userEmail?: string; // Masked in logs
    ipAddress?: string;
    userAgent?: string;
    resource?: string;
    action?: string;
    status: 'success' | 'failure';
    metadata?: Record<string, any>;
}

export interface AuditLogStorage {
    save(event: AuditEvent): Promise<void>;
    query(filters: AuditQueryFilters): Promise<AuditEvent[]>;
}

export interface AuditQueryFilters {
    userId?: string;
    eventType?: AuditEventType;
    startTime?: number;
    endTime?: number;
    status?: 'success' | 'failure';
    limit?: number;
}

/**
 * In-memory audit log storage (default)
 * Replace with database implementation for production
 */
class InMemoryAuditStorage implements AuditLogStorage {
    private events: AuditEvent[] = [];
    private maxEvents = 10000; // Prevent memory bloat

    async save(event: AuditEvent): Promise<void> {
        this.events.push(event);

        // Keep only recent events
        if (this.events.length > this.maxEvents) {
            this.events = this.events.slice(-this.maxEvents);
        }
    }

    async query(filters: AuditQueryFilters): Promise<AuditEvent[]> {
        let results = [...this.events];

        if (filters.userId) {
            results = results.filter(e => e.userId === filters.userId);
        }

        if (filters.eventType) {
            results = results.filter(e => e.eventType === filters.eventType);
        }

        if (filters.status) {
            results = results.filter(e => e.status === filters.status);
        }

        if (filters.startTime) {
            results = results.filter(e => e.timestamp >= filters.startTime!);
        }

        if (filters.endTime) {
            results = results.filter(e => e.timestamp <= filters.endTime!);
        }

        // Sort by timestamp descending (newest first)
        results.sort((a, b) => b.timestamp - a.timestamp);

        // Apply limit
        if (filters.limit) {
            results = results.slice(0, filters.limit);
        }

        return results;
    }
}

/**
 * Audit logger with pluggable storage backend
 */
class AuditLogger {
    private storage: AuditLogStorage;
    private enabled = process.env.ENABLE_AUDIT_LOG !== 'false';

    constructor(storage?: AuditLogStorage) {
        this.storage = storage || new InMemoryAuditStorage();
    }

    /**
     * Set custom storage backend (e.g., database)
     */
    setStorage(storage: AuditLogStorage) {
        this.storage = storage;
    }

    /**
     * Mask email for PII compliance
     */
    private maskEmail(email?: string): string | undefined {
        if (!email) return undefined;
        return email.replace(/^(.)(.*)(@.*)$/, (_, a, b, c) => a + b.replace(/./g, '*') + c);
    }

    /**
     * Log an audit event
     */
    async log(event: Omit<AuditEvent, 'id' | 'timestamp'>): Promise<void> {
        if (!this.enabled) return;

        const auditEvent: AuditEvent = {
            id: this.generateId(),
            timestamp: Date.now(),
            ...event,
            userEmail: this.maskEmail(event.userEmail),
        };

        await this.storage.save(auditEvent);

        // Also log to structured logger for immediate visibility
        const { logger } = await import('./logger');
        logger.info(`[AUDIT] ${event.eventType}`, {
            eventId: auditEvent.id,
            userId: event.userId,
            status: event.status,
            resource: event.resource,
        });
    }

    /**
     * Query audit logs
     */
    async query(filters: AuditQueryFilters): Promise<AuditEvent[]> {
        return this.storage.query(filters);
    }

    /**
     * Generate unique event ID
     */
    private generateId(): string {
        return `audit_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }
}

export const auditLog = new AuditLogger();

/**
 * Convenience functions for common audit events
 */
export const auditHooks = {
    async logLogin(userId: string, userEmail: string, provider: string, ipAddress?: string) {
