export type ErrorCode =
    | 'VALIDATION_ERROR'
    | 'AUTH_ERROR'
    | 'NOT_FOUND'
    | 'RATE_LIMIT_EXCEEDED'
    | 'EXTERNAL_SERVICE_ERROR'
    | 'TIMEOUT_ERROR'
    | 'CONFIG_ERROR'
    | 'INTERNAL_SERVER_ERROR'
    | 'BAD_REQUEST';

export class AppError extends Error {
    public readonly code: ErrorCode;
    public readonly httpStatus: number;
    public readonly retryable: boolean;
    public readonly details?: any;

    constructor(opts: {
        code: ErrorCode;
        message: string;
        httpStatus?: number;
        retryable?: boolean;
        details?: any;
    }) {
        super(opts.message);
        this.name = 'AppError';
        this.code = opts.code;
        this.httpStatus = opts.httpStatus || 500;
        this.retryable = opts.retryable || false;
        this.details = opts.details;

        // Restore prototype chain for instance checks
        Object.setPrototypeOf(this, AppError.prototype);
    }

    static isAppError(error: any): error is AppError {
        return error instanceof AppError;
    }
}

export class ValidationError extends AppError {
    constructor(message: string, details?: any) {
        super({
            code: 'VALIDATION_ERROR',
            message,
            httpStatus: 400,
            retryable: false,
            details,
        });
    }
}

export class AuthError extends AppError {
    constructor(message: string = 'Unauthorized') {
        super({
            code: 'AUTH_ERROR',
            message,
            httpStatus: 401,
            retryable: false,
        });
    }
}

export class NotFoundError extends AppError {
    constructor(message: string = 'Resource not found') {
        super({
            code: 'NOT_FOUND',
            message,
            httpStatus: 404,
            retryable: false,
        });
    }
}

export class RateLimitError extends AppError {
    constructor(message: string = 'Rate limit exceeded') {
        super({
            code: 'RATE_LIMIT_EXCEEDED',
            message,
            httpStatus: 429,
            retryable: true,
        });
    }
}

export class ExternalServiceError extends AppError {
    constructor(message: string, details?: any) {
        super({
            code: 'EXTERNAL_SERVICE_ERROR',
            message,
            httpStatus: 502,
            retryable: true,
            details,
        });
    }
}

export class TimeoutError extends AppError {
    constructor(message: string = 'Request timed out') {
        super({
            code: 'TIMEOUT_ERROR',
            message,
            httpStatus: 504,
            retryable: true,
        });
    }
}

export class ConfigError extends AppError {
    constructor(message: string) {
        super({
            code: 'CONFIG_ERROR',
            message,
            httpStatus: 500,
            retryable: false,
        });
    }
}

export function normalizeError(error: unknown): AppError {
    if (AppError.isAppError(error)) {
        return error;
    }

    if (error instanceof Error) {
        // Handle specific error types if needed (e.g. database errors)
        const message = error.message;

        // Example: Detect timeouts
        if (message.includes('timeout') || message.includes('ETIMEDOUT')) {
            return new TimeoutError(message);
        }

        return new AppError({
            code: 'INTERNAL_SERVER_ERROR',
            message: process.env.NODE_ENV === 'production'
                ? 'An unexpected error occurred.'
                : message,
            httpStatus: 500,
            details: process.env.NODE_ENV === 'development' ? { stack: error.stack } : undefined,
        });
    }

    return new AppError({
        code: 'INTERNAL_SERVER_ERROR',
        message: 'An unknown error occurred.',
        httpStatus: 500,
    });
}

import { NextRequest } from 'next/server';
import { createErrorResponse } from './api-utils';

/**
 * Form security utilities for bot/spam protection
 */

export interface HoneypotValidation {
    isBot: boolean;
    field?: string;
}

export interface FormSecurityConfig {
    maxPayloadSize?: number;      // Max request size in bytes (default: 10KB)
    minSubmitTime?: number;        // Min time to submit in ms (default: 2000ms)
    maxSubmitTime?: number;        // Max time to submit in ms (default: 5 minutes)
    honeypotFields?: string[];     // Honeypot field names
    requiredContentType?: string;  // Required content-type
}

const DEFAULT_CONFIG: Required<FormSecurityConfig> = {
    maxPayloadSize: 10 * 1024,     // 10KB
    minSubmitTime: 2000,           // 2 seconds
    maxSubmitTime: 5 * 60 * 1000,  // 5 minutes
    honeypotFields: ['website', 'url', 'homepage', 'phone_number'],
    requiredContentType: 'application/json'
};

/**
 * Check honeypot fields for bot detection
 */
export function checkHoneypot(
    body: any,
    honeypotFields: string[] = DEFAULT_CONFIG.honeypotFields
): HoneypotValidation {
    for (const field of honeypotFields) {
        if (body[field] !== undefined && body[field] !== '') {
            return { isBot: true, field };
        }
    }
    return { isBot: false };
}

/**
 * Validate request content-type
 */
export function validateContentType(
    request: NextRequest,
    requiredType: string = DEFAULT_CONFIG.requiredContentType
): boolean {
    const contentType = request.headers.get('content-type');
    return contentType?.includes(requiredType) ?? false;
}

/**
 * Validate request payload size
 */
export async function validatePayloadSize(
    request: NextRequest,
    maxSize: number = DEFAULT_CONFIG.maxPayloadSize
): Promise<{ valid: boolean; size?: number }> {
    const contentLength = request.headers.get('content-length');

    if (contentLength) {
        const size = parseInt(contentLength);
        if (size > maxSize) {
            return { valid: false, size };
        }
    }

    return { valid: true };
}

/**
 * Validate time-to-submit heuristic
 * Helps detect automated form submissions
 */
export function validateSubmitTime(
    formStartTime: number,
    config: Pick<FormSecurityConfig, 'minSubmitTime' | 'maxSubmitTime'> = {}
): { valid: boolean; duration: number } {
    const minTime = config.minSubmitTime ?? DEFAULT_CONFIG.minSubmitTime;
    const maxTime = config.maxSubmitTime ?? DEFAULT_CONFIG.maxSubmitTime;

    const duration = Date.now() - formStartTime;

    if (duration < minTime) {
        // Too fast - likely a bot
        return { valid: false, duration };
    }

    if (duration > maxTime) {
        // Too slow - form might be stale
        return { valid: false, duration };
    }

    return { valid: true, duration };
}

/**
 * Sanitize form data for logging
 * Removes sensitive user content
 */
export function sanitizeForLogging(data: any): any {
    const sensitiveFields = [
        'message',
        'comment',
        'description',
        'content',
        'body',
        'text',
        'password',
        'token',
        'secret',
        'apiKey',
        'creditCard',
        'ssn'
    ];

    const sanitized: any = {};

    for (const [key, value] of Object.entries(data)) {
        const isSensitive = sensitiveFields.some(field =>
            key.toLowerCase().includes(field.toLowerCase())
        );

        if (isSensitive) {
            sanitized[key] = '[REDACTED]';
        } else {
            sanitized[key] = value;
        }
    }

    return sanitized;
}

/**
 * Comprehensive form security validation
 */
export async function validateFormSecurity(
    request: NextRequest,
    body: any,
    config: FormSecurityConfig = {}
): Promise<{
    valid: boolean;
    error?: {
        code: string;
        message: string;
        retryable: boolean;
    };
}> {
    const finalConfig = { ...DEFAULT_CONFIG, ...config };

    // 1. Content-Type validation
    if (!validateContentType(request, finalConfig.requiredContentType)) {
        return {
            valid: false,
            error: {
                code: 'INVALID_CONTENT_TYPE',
                message: 'Invalid content-type. Expected application/json',
                retryable: false
            }
        };
    }

    // 2. Payload size validation
    const sizeCheck = await validatePayloadSize(request, finalConfig.maxPayloadSize);
    if (!sizeCheck.valid) {
        return {
            valid: false,
            error: {
                code: 'PAYLOAD_TOO_LARGE',
                message: `Request payload exceeds maximum size of ${finalConfig.maxPayloadSize} bytes`,
                retryable: false
            }
        };
    }

    // 3. Honeypot validation
    const honeypotCheck = checkHoneypot(body, finalConfig.honeypotFields);
    if (honeypotCheck.isBot) {
        // Don't tell the bot it was detected - return success
        // This will be handled by the caller
        return { valid: true };
    }

    // 4. Time-to-submit validation (if timestamp provided)
    if (body._formStartTime) {
        const timeCheck = validateSubmitTime(body._formStartTime, {
            minSubmitTime: finalConfig.minSubmitTime,
            maxSubmitTime: finalConfig.maxSubmitTime
        });

        if (!timeCheck.valid) {
            if (timeCheck.duration < finalConfig.minSubmitTime) {
                // Too fast - likely bot, but don't tell them
                return { valid: true };
            } else {
                // Too slow - form expired
                return {
                    valid: false,
                    error: {
                        code: 'FORM_EXPIRED',
                        message: 'Form submission expired. Please refresh and try again.',
                        retryable: true
                    }
                };
            }
        }
    }

    return { valid: true };
}

/**
 * Common honeypot field names to use in forms
 */
export const HONEYPOT_FIELDS = {
    website: 'website',           // Most common
    url: 'url',
    homepage: 'homepage',
    phone_number: 'phone_number', // Note: different from 'phone'
    company_url: 'company_url',
    fax: 'fax'
} as const;

/**
 * Generate form start timestamp for client-side
 */
export function generateFormStartTime(): number {
    return Date.now();
}

/**
 * Structured logging utility with PII-safe output
 * Outputs JSON logs for production observability
 */

export type LogLevel = 'debug' | 'info' | 'warn' | 'error';

export interface LogContext {
    requestId?: string;
    route?: string;
    method?: string;
    status?: number | string;
    userId?: string;
    duration?: number;
    [key: string]: any;
}

class Logger {
    private isDevelopment = process.env.NODE_ENV === 'development';

    private formatLog(level: LogLevel, message: string, context?: LogContext) {
        const timestamp = new Date().toISOString();
        const logEntry = {
            timestamp,
            level,
            message,
            ...context,
        };

        return JSON.stringify(logEntry);
    }

    private maskEmail(email?: string): string | undefined {
        if (!email) return undefined;
        return email.replace(/^(.)(.*)(@.*)$/, (_, a, b, c) => a + b.replace(/./g, '*') + c);
    }

    private sanitizeContext(context?: LogContext): LogContext | undefined {
        if (!context) return undefined;

        const sanitized = { ...context };

        // Mask any email fields
        if (sanitized.email) {
            sanitized.email = this.maskEmail(sanitized.email);
        }
        if (sanitized.userEmail) {
            sanitized.userEmail = this.maskEmail(sanitized.userEmail);
        }

        // Remove sensitive fields
        delete sanitized.password;
        delete sanitized.token;
        delete sanitized.secret;
        delete sanitized.apiKey;

        return sanitized;
    }

    debug(message: string, context?: LogContext) {
        if (this.isDevelopment) {
            console.debug(this.formatLog('debug', message, this.sanitizeContext(context)));
        }
    }

    info(message: string, context?: LogContext) {
        console.info(this.formatLog('info', message, this.sanitizeContext(context)));
    }

    warn(message: string, context?: LogContext) {
        console.warn(this.formatLog('warn', message, this.sanitizeContext(context)));
    }

    error(message: string, context?: LogContext) {
        console.error(this.formatLog('error', message, this.sanitizeContext(context)));
    }

    /**
     * Log HTTP request/response
     */
    http(context: LogContext) {
        const { method, route, status, duration, requestId } = context;
        const message = `${method} ${route} ${status}`;

        if (typeof status === 'number' && status >= 500) {
            this.error(message, { requestId, duration, status });
        } else if (typeof status === 'number' && status >= 400) {
            this.warn(message, { requestId, duration, status });
        } else {
            this.info(message, { requestId, duration, status });
        }
    }
}

export const logger = new Logger();

import { AppError, TimeoutError } from './errors';
import { logger } from './logger';

interface RetryOptions {
    maxAttempts?: number;
    baseDelayMs?: number;
    maxDelayMs?: number;
    timeoutMs?: number;
    jitter?: boolean;
    retryableErrors?: string[]; // Error codes to retry. If empty, uses error.retryable
}

const DEFAULT_OPTIONS: Required<Omit<RetryOptions, 'retryableErrors'>> = {
    maxAttempts: 3,
    baseDelayMs: 200,
    maxDelayMs: 2000,
    timeoutMs: 5000,
    jitter: true,
};

async function sleep(ms: number) {
    return new Promise((resolve) => setTimeout(resolve, ms));
}

function calculateDelay(attempt: number, opts: typeof DEFAULT_OPTIONS): number {
    const exponentialDelay = Math.min(
        opts.baseDelayMs * Math.pow(2, attempt),
        opts.maxDelayMs
    );

    if (opts.jitter) {
        // Add random jitter between 0 and 20% of the delay
        return exponentialDelay * (1 + Math.random() * 0.2);
    }
    return exponentialDelay;
}

export async function withRetry<T>(
    operation: () => Promise<T>,
    options: RetryOptions = {},
    operationName: string = 'Operation'
): Promise<T> {
    const opts = { ...DEFAULT_OPTIONS, ...options };
    let attempt = 0;

    while (true) {
        attempt++;
        try {
            // Execute operation with timeout
            if (opts.timeoutMs > 0) {
                const controller = new AbortController();
                const timeoutId = setTimeout(() => controller.abort(), opts.timeoutMs);

                try {
                    // Race the operation against the timeout
                    // Note: The operation function needs to support signal manually if passed,
                    // but for general async valid promises, we can just race the result.
                    // Ideally pass signal to operation, but keeping signature simple for now.
                    // For proper timeout support, the underlying fetch should respect the signal.

                    const result = await Promise.race([
                        operation(),
                        new Promise<T>((_, reject) => {
                            // If the timeout fires, we reject
                            // We attach the timeout listener to the controller's signal abort event
                            controller.signal.addEventListener('abort', () => {
                                reject(new TimeoutError(`${operationName} timed out after ${opts.timeoutMs}ms`));
                            });
                        })
                    ]);
                    clearTimeout(timeoutId);
                    return result;

                } catch (_e) {
                    clearTimeout(timeoutId);
                    throw _e;
                }
            } else {
                return await operation();
            }

        } catch (error: any) {
            // Determine if we should retry
            let shouldRetry = false;

            if (attempt < opts.maxAttempts) {
                if (AppError.isAppError(error)) {
                    shouldRetry = error.retryable;
                    if (opts.retryableErrors && !opts.retryableErrors.includes(error.code)) {
                        shouldRetry = false;
                    }
                } else {
                    // Normalize unknown errors check? 
                    // Usually we treat network errors are retryable. 
                    // Simple check for now:
                    if (error.code === 'ECONNRESET' || error.message.includes('network') || error.name === 'TimeoutError') {
                        shouldRetry = true;
                    }
                }
            }

            if (!shouldRetry) {
                throw error;
            }

            const delay = calculateDelay(attempt, opts);
            logger.warn(`Retrying ${operationName}`, {
                attempt,
                maxAttempts: opts.maxAttempts,
                error: error.message,
                delay
            });

            await sleep(delay);
        }
    }
}

/**
 * Client-side resilient fetch utility
 */

export interface SafeFetchOptions<T = any> extends RequestInit {
    timeoutMs?: number;
    maxRetries?: number;
    fallbackData?: T | null;
}

const DEFAULT_TIMEOUT = 5000;
const DEFAULT_RETRIES = 1;

export async function safeFetch<T>(
    input: RequestInfo | URL,
    options: SafeFetchOptions<T> = {}
): Promise<T | null> {
    const {
        timeoutMs = DEFAULT_TIMEOUT,
        maxRetries = DEFAULT_RETRIES,
        fallbackData = null,
        ...fetchOptions
    } = options;

    let attempt = 0;

    while (attempt <= maxRetries) {
        const controller = new AbortController();
        const id = setTimeout(() => controller.abort(), timeoutMs);

        try {
            const response = await fetch(input, {
                ...fetchOptions,
                signal: controller.signal,
            });

            clearTimeout(id);

            if (!response.ok) {
                // If it's a 4xx error, don't retry, just return fallback
                if (response.status >= 400 && response.status < 500) {
                    console.warn(`[SafeFetch] Request failed with client error: ${response.status}`);
                    return fallbackData;
                }
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const json = await response.json();
            // Handle consistent API envelope if present
            if (json && json.status === 'success') {
                return json.data;
            }
            return json;

        } catch (error: unknown) {
            const err = error as Error;
            clearTimeout(id);
            attempt++;

            const isTimeout = err.name === 'AbortError';
            const isNetworkError = err.name === 'TypeError' || err.message.includes('network');

            if (attempt <= maxRetries && (isTimeout || isNetworkError)) {
                console.warn(`[SafeFetch] Attempt ${attempt} failed, retrying...`, err.message);
                continue;
            }

            console.error('[SafeFetch] All attempts failed:', err.message);
            return fallbackData;
        }
    }

    return fallbackData;
}

