/**
 * Validates if a URL is safe for redirection.
 * Prevents Open Redirect vulnerabilities.
 * 
 * @param url The URL to validate
 * @param allowExternal Allow specific external domains (default: false)
 */
export function isSafeRedirect(url: string, allowedDomains: string[] = []): boolean {
    if (!url) return false;

    // Allow relative paths (start with / but not //)
    if (url.startsWith('/') && !url.startsWith('//') && !url.includes('\\')) {
        return true;
    }

    try {
        const urlObj = new URL(url);
        // Block non-http protocols (e.g. javascript:)
        if (urlObj.protocol !== 'http:' && urlObj.protocol !== 'https:') {
            return false;
        }

        // If external domains are allowed, check against allowlist
        if (allowedDomains.includes(urlObj.hostname)) {
            return true;
        }

        // Otherwise, allow only same origin (requires NEXT_PUBLIC_SITE_URL or similar logic, 
        // but robustly, we defaults to false for absolute URLs unless whitelisted)
        return false;
    } catch {
        return false;
    }
}

/**
 * Generates a cryptographiclly secure random number between min and max (inclusive).
 * Replaces Math.random() to satisfy CodeQL.
 */
export function secureRandomInt(min: number, max: number): number {
    const range = max - min + 1;
    const array = new Uint32Array(1);
    crypto.getRandomValues(array);
    return min + (array[0] % range);
}

/**
 * Sanitizes JSON-LD data to safely embed in <script> tags.
 * Escapes </script> to prevent XSS.
 */
export function safeJsonLd(data: Record<string, any>): string {
    const json = JSON.stringify(data);
    return json.replace(/<\/script>/g, '<\\/script>');
}

import { withRetry } from '@/lib/retry';

// Initialize Vault client lazily to avoid loading 'node-vault' in Edge Runtime
// Expects VAULT_ADDR and VAULT_TOKEN to be set in environment variables
// in non-local environments.

/**
 * Reads a secret from HashiCorp Vault KV v2 engine.
 * @param path The path to the secret (e.g. "nascent-zodiac/prod")
 * @returns The secret data object
 */
export async function getVaultSecret(path: string): Promise<Record<string, string> | null> {
    try {
        // Dynamically import node-vault to prevent Edge Runtime crashes
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const vaultModule: any = await import('node-vault');
        const vault = vaultModule.default;

        const client = vault({
            endpoint: process.env.VAULT_ADDR,
            token: process.env.VAULT_TOKEN,
        });

        // KV v2 requires adding "data/" after the mount point (usually "secret")
        // We assume the default mount point "secret" for simplicity, or the user
        // provides "secret/data/..."

        // If path doesn't contain 'data', we might need to inject it if we want to be helpful,
        // but strictly standard Vault paths are explicit.
        // Let's support the standard `read` method which node-vault handles.

        const response: any = await withRetry(
            () => client.read(path),
            { maxAttempts: 3, timeoutMs: 3000 },
            'VaultRead'
        );

        // Vault KV v2 returns data in response.data.data
        if (response && response.data && response.data.data) {
            return response.data.data as Record<string, string>;
        }

        // Fallback for KV v1 or direct read
        if (response && response.data) {
            return response.data as Record<string, string>;
        }

        return null;
    } catch (error) {
        console.error(`Vault Error reading path ${path}:`, error);
        return null;
    }
}

import { getRedis } from '@/lib/redis';
import { withRetry } from '@/lib/retry';
import { getSecret } from '@/secrets';

// Optional imports
let Resend: any = null;
try {
    // @ts-ignore
    Resend = require('resend').Resend;
} catch (_e) { }

export interface ContactSubmission {
    firstName: string;
    lastName: string;
    email: string;
    message: string;
    website?: string;
}

export interface SubmissionResult {
    success: boolean;
    submissionId: string;
    saved: boolean;
    notified: boolean;
    message: string;
}

export class LeadService {

    /**
     * Process a new contact form submission
     */
    static async submitContactForm(data: ContactSubmission): Promise<SubmissionResult> {
        const { firstName, lastName, email, message } = data;

        // Generate Metadata
        const submissionId = `SOV-${Math.random().toString(36).substring(2, 9).toUpperCase()}`;
        const timestamp = new Date().toISOString();

        // Init Dependencies
        const redis = await getRedis();
        const resendApiKey = await getSecret('RESEND_API_KEY');
        const resendFrom = await getSecret('RESEND_FROM_EMAIL');
        const resendTo = await getSecret('RESEND_TO_EMAIL');

        let saved = false;
        let notified = false;

        // 1. Storage (Redis)
        if (redis) {
            try {
                const leadData = {
                    submissionId,
                    firstName,
                    lastName,
                    email,
                    message,
                    timestamp,
                    source: 'contact_form',
                    status: 'new',
                };

                await withRetry(async () => {
                    const pipe = redis.pipeline();
                    pipe.set(`lead:${submissionId}`, JSON.stringify(leadData));
                    pipe.lpush('leads:all', submissionId);
                    pipe.set(`lead:email:${email}`, submissionId);
                    pipe.expire(`lead:${submissionId}`, 31536000);
                    await pipe.exec();
                }, { maxAttempts: 3, timeoutMs: 2000 }, 'RedisSaveLead');

                console.log(`[LeadService] Lead saved: ${submissionId}`);
                saved = true;
            } catch (err) {
                console.error(`[LeadService] Redis Error for ${submissionId}:`, err);
            }
        }

        // 2. Notification (Resend)
        if (Resend && resendApiKey && resendFrom && resendTo) {
            try {
                const resend = new Resend(resendApiKey);
                await withRetry(async () => {
                    await resend.emails.send({
                        from: resendFrom,
                        to: resendTo,
                        subject: `≡ƒöö New Lead: ${firstName} ${lastName} - ${submissionId}`,
                        html: this.generateEmailHtml(data, submissionId, timestamp)
                    });
                }, { maxAttempts: 3, timeoutMs: 5000 }, 'ResendEmail');

                console.log(`[LeadService] Email sent for ${submissionId}`);
                notified = true;
            } catch (err) {
                console.error(`[LeadService] Email Error for ${submissionId}:`, err);
            }
        }

        return {
            success: true,
            submissionId,
            saved,
            notified,
            message: 'Briefing received and encrypted in sovereign storage.'
        };
    }

    private static generateEmailHtml(data: ContactSubmission, id: string, timestamp: string): string {
        return `
            <!DOCTYPE html>
            <html>
            <body style="font-family: sans-serif; line-height: 1.6; color: #333;">
                <div style="max-width: 600px; margin: 0 auto; padding: 20px;">
                    <h2 style="color: #4f46e5; border-bottom: 2px solid #eee; padding-bottom: 10px;">New Lead Captured</h2>
                    <p><strong>ID:</strong> <code>${id}</code></p>
                    <p><strong>Name:</strong> ${data.firstName} ${data.lastName}</p>
                    <p><strong>Email:</strong> <a href="mailto:${data.email}">${data.email}</a></p>
                    <div style="background: #f9fafb; padding: 15px; border-radius: 8px; margin: 20px 0;">
                        <strong>Message:</strong><br/>
                        ${data.message.replace(/\n/g, '<br>')}
                    </div>
                    <p style="color: #666; font-size: 12px;">Submitted: ${new Date(timestamp).toLocaleString()}</p>
                </div>
            </body>
            </html>
        `;
    }
}

/**
 * Smooth Anchor Scrolling Utility
 * 
 * Handles client-side anchor scrolling with proper offset for sticky header
 */

'use client';

/**
 * Get the total offset needed for sticky header + breadcrumb
 */
export function getScrollOffset(): number {
    if (typeof window === 'undefined') return 0;

    const style = getComputedStyle(document.documentElement);
    const headerHeight = parseInt(style.getPropertyValue('--header-height') || '70');
    const breadcrumbHeight = parseInt(style.getPropertyValue('--breadcrumb-height') || '48');

    return headerHeight + breadcrumbHeight + 16; // +16px for breathing room
}

/**
 * Smooth scroll to an element by ID
 */
export function scrollToElement(elementId: string, behavior: ScrollBehavior = 'smooth'): void {
    const element = document.getElementById(elementId);
    if (!element) {
        console.warn(`Element with id "${elementId}" not found`);
        return;
    }

    const offset = getScrollOffset();
    const elementPosition = element.getBoundingClientRect().top + window.pageYOffset;
    const offsetPosition = elementPosition - offset;

    window.scrollTo({
        top: offsetPosition,
        behavior
    });
}

/**
 * Handle anchor navigation
 * - If on same page: scroll to section
 * - If on different page: navigate with hash, then scroll after load
 */
export function handleAnchorClick(
    href: string,
    currentPath: string,
    router: any, // Next.js router
    locale: string
): void {
    const url = new URL(href, window.location.origin);
    const targetPath = url.pathname;
    const targetHash = url.hash.slice(1); // Remove #

    // Check if we're on the same page
    const isSamePage = currentPath === targetPath || targetPath === `/${locale}${currentPath}`;

    if (isSamePage && targetHash) {
        // Same page: just scroll
        scrollToElement(targetHash);
        // Update URL without reload
        window.history.pushState(null, '', `#${targetHash}`);
    } else if (targetHash) {
        // Different page: navigate then scroll
        router.push(href);
        // Scroll will happen after navigation via useEffect in layout
    } else {
        // No hash: normal navigation
        router.push(href);
    }
}

/**
 * Hook to handle hash scrolling on page load
 */
export function useHashScroll() {
    if (typeof window === 'undefined') return;

    // Scroll to hash on initial load
    const hash = window.location.hash.slice(1);
    if (hash) {
        // Wait for DOM to be ready
        setTimeout(() => {
            scrollToElement(hash, 'auto'); // Use 'auto' for instant scroll on load
        }, 100);
    }
}
